from machine import Pin, I2C
from ssd1306 import SSD1306_I2C
import framebuf

from cardialink_logo_binary import binary_data

# OLED settings
oled_width = 128
oled_height = 64

# I2C connection
i2c = I2C(1, scl=Pin(15), sda=Pin(14), freq=400000)

# OLED object
oled = SSD1306_I2C(oled_width, oled_height, i2c)

# FrameBuffer for logo
logo = framebuf.FrameBuffer(bytearray(binary_data), oled_width, oled_height, framebuf.MONO_VLSB)

# clear screen and print logo
oled.fill(0)
oled.blit(logo, 0, 0)  # Shows logo in upper right corner
oled.show()

